const Discord = require("discord.js")
const { Command } = require("discord.js-commando")
const roblox = require("noblox.js")

module.exports = class exile extends Command {
  constructor(client){
    super(client, {
      name: "exile",
      description: "Exile from group.",
      
      group: "administrator",
      memberName: "exile",
      
      args: [
        {
          type: "string",
          prompt: "What is their Roblox username?",
          key: "username",
        }
      ]
    })
  }
  
  hasPermission(message){
    if(message.member.roles.find(role => role.name == "Admin")){
      return true 
    } else {
      return "Sorry :persevere:! You must be an administrator to use this command!"
    }
  }
  
  async run(message, { username }){
    
    // Primary Variables
    const sentMessage = await message.reply(`Allow me to work my magic.. :sleeping:`)
    
    const robloxToken = "_|WARNING:-DO-NOT-SHARE-THIS.--Sharing-this-will-allow-someone-to-log-in-as-you-and-to-steal-your-ROBUX-and-items.|_"
    const groupID = "8473766"
    
    let playerType 
    let playerName 
    let playerID 
    
    if(Number(username)){
      playerType = "ID"
    } else {
      playerType = "USERNAME"
    }
    
    
    // Primary Functions 
    await roblox.setCookie(robloxToken)
    
    try { 
      if(playerType == "ID"){
        playerName = await roblox.getUsernameFromId(username)
        playerID = await roblox.getIdFromUsername(playerName)
      } else if(playerType == "USERNAME"){
        playerID = await roblox.getIdFromUsername(username)
        playerName = await roblox.getUsernameFromId(playerID)
      }
    } catch(error){
      sentMessage.edit(`${message.author}, :construction: There has been an error! \`\`\`js\n${error}\`\`\``)
      return
    }
    
    try {
      await roblox.exile(groupID, playerID).then(() => {
        sentMessage.edit(`${message.author}, :raised_hands: Successfully exiled **${playerName}**!`)
      }).catch(error => {
        sentMessage.edit(`${message.author}, :construction: There has been an error! \`\`\`js\n${error}\`\`\``)
        return
      })
    } catch(error){
      sentMessage.edit(`${message.author}, :construction: There has been an error! \`\`\`js\n${error}\`\`\``)
      return
    }
    
    
  }
}